mapper.select:
select p.id, p.name, if(rel.menu_id is null, false, true) as checked from tb_menu p left join tb_role_menu_rel rel on rel.role_id = #{roleId} and rel.menu_id = p.id where sys_id = ( select sys_id from tb_role where id = #{roleId})
and pid is null
and pid = #{pid}

select p.id, p.name, if(rel.api_id is null, false, true) as checked from tb_api p left join tb_role_api_rel rel on rel.role_id = #{roleId} and rel.api_id = p.id where sys_id = ( select sys_id from tb_role where id = #{roleId})
and pid is null
and pid = #{pid}
